import React, { useState, useEffect } from "react";
import { TextField, Button, Checkbox, FormControlLabel, Typography, Grid, Box, IconButton, MenuItem, Select, InputLabel, FormControl } from '@mui/material';
import { Delete as DeleteIcon, Edit as EditIcon, Save as SaveIcon } from '@mui/icons-material';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider, DatePicker } from '@mui/x-date-pickers';

// Task Component
const Task = ({ task, onToggleComplete, onDelete, onEdit, onSave }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editText, setEditText] = useState(task.title);
  const [editDueDate, setEditDueDate] = useState(task.dueDate);

  const handleSave = () => {
    onSave(task.id, editText, editDueDate);
    setIsEditing(false);
  };

  // Check if the task is overdue
  const isOverdue = new Date(task.dueDate) < new Date() && !task.completed;

  return (
    <Box display="flex" alignItems="center" justifyContent="space-between" mb={2} p={2} border={1} borderColor={isOverdue ? 'red' : 'gray'} borderRadius={2} bgcolor={isOverdue ? '#ffcccc' : '#f5f5f5'}>
      <Box display="flex" alignItems="center">
        <FormControlLabel
          control={<Checkbox checked={task.completed} onChange={() => onToggleComplete(task.id)} />}
          label={isEditing ? (
            <TextField
              value={editText}
              onChange={(e) => setEditText(e.target.value)}
              variant="outlined"
              size="small"
              style={{ marginRight: 10 }}
            />
          ) : (
            <Typography variant="body1" style={{ textDecoration: task.completed ? 'line-through' : 'none' }}>
              {task.title}
            </Typography>
          )}
        />
      </Box>

      <Box display="flex" alignItems="center">
        <Typography variant="body2" style={{ marginRight: 10 }}>
          Due: {isEditing ? (
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                value={editDueDate}
                onChange={(date) => setEditDueDate(date)}
                renderInput={(params) => <TextField {...params} variant="outlined" size="small" />}
              />
            </LocalizationProvider>
          ) : (
            new Date(task.dueDate).toLocaleDateString()
          )}
        </Typography>

        {isEditing ? (
          <IconButton onClick={handleSave}>
            <SaveIcon />
          </IconButton>
        ) : (
          <IconButton onClick={() => setIsEditing(true)}>
            <EditIcon />
          </IconButton>
        )}
        <IconButton onClick={() => onDelete(task.id)}>
          <DeleteIcon />
        </IconButton>
      </Box>
    </Box>
  );
};

// Main App Component
const App = () => {
  const [tasks, setTasks] = useState(() => {
    const savedTasks = localStorage.getItem("tasks");
    return savedTasks ? JSON.parse(savedTasks) : [];
  });
  const [newTask, setNewTask] = useState("");
  const [category, setCategory] = useState("");
  const [dueDate, setDueDate] = useState(null);

  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  const handleAddTask = () => {
    if (newTask.trim() === "" || !dueDate) return;

    const newTaskObj = {
      id: Date.now(),
      title: newTask,
      completed: false,
      category: category || "General",
      dueDate: dueDate.toISOString(),
    };

    setTasks((prevTasks) => [...prevTasks, newTaskObj]);
    setNewTask("");
    setCategory("");
    setDueDate(null);
  };

  const handleToggleComplete = (taskId) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === taskId
          ? { ...task, completed: !task.completed }
          : task
      )
    );
  };

  const handleDeleteTask = (taskId) => {
    setTasks((prevTasks) => prevTasks.filter((task) => task.id !== taskId));
  };

  const handleEditTask = (taskId, newTitle, newDueDate) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === taskId ? { ...task, title: newTitle, dueDate: newDueDate } : task
      )
    );
  };

  return (
    <Box display="flex" flexDirection="column" alignItems="center" style={{ padding: 20, maxWidth: 600, margin: "0 auto" }}>
      <Typography variant="h4" gutterBottom>
        Task Manager
      </Typography>

      {/* Task Input */}
      <Box mb={2} display="flex" alignItems="center" justifyContent="space-between" width="100%">
        <TextField
          label="New Task"
          variant="outlined"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          style={{ marginRight: 10 }}
          fullWidth
        />
        <FormControl variant="outlined" style={{ minWidth: 120 }}>
          <InputLabel>Category</InputLabel>
          <Select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            label="Category"
          >
            <MenuItem value="Work">Work</MenuItem>
            <MenuItem value="Personal">Personal</MenuItem>
            <MenuItem value="General">General</MenuItem>
          </Select>
        </FormControl>
      </Box>

      {/* Due Date Picker */}
      <Box mb={2} width="100%">
        <LocalizationProvider dateAdapter={AdapterDateFns}>
          <DatePicker
            label="Due Date"
            value={dueDate}
            onChange={(date) => setDueDate(date)}
            renderInput={(params) => <TextField {...params} variant="outlined" size="small" fullWidth />}
          />
        </LocalizationProvider>
      </Box>

      {/* Add Task Button */}
      <Button
        variant="contained"
        color="primary"
        onClick={handleAddTask}
        style={{ width: "100%", padding: "10px", fontWeight: "bold" }}
      >
        Add Task
      </Button>

      {/* Task List */}
      <Box mt={3} width="100%">
        {tasks.length === 0 ? (
          <Typography>No tasks available!</Typography>
        ) : (
          tasks.map((task) => (
            <Task
              key={task.id}
              task={task}
              onToggleComplete={handleToggleComplete}
              onDelete={handleDeleteTask}
              onSave={handleEditTask}
            />
          ))
        )}
      </Box>
    </Box>
  );
};

export default App;
