import React from 'react';
import './HowItWorks.css';

const steps = [
  {
    title: 'Pick your first challenge',
    description:
      'Browse through our curated list of industry-relevant challenges and choose one that matches your interests and skill level.',
    icon: '🎯',
  },
  {
    title: 'Build your profile & earn badges',
    description:
      'Complete challenges to earn badges and build a dynamic profile that showcases your skills to potential employers.',
    icon: '🛠️',
  },
  {
    title: 'Collaborate, freelance, and get scouted',
    description:
      'Join teams, take on freelance jobs, and get discovered by companies looking for talent just like you.',
    icon: '🤝',
  },
];

const HowItWorks = () => {
  return (
    <div className="how-it-works-container">
      <h2 className="fade-in">How It Works</h2>
      <div className="steps-container fade-in">
        {steps.map((step, index) => (
          <div key={index} className="step">
            <div className="icon">{step.icon}</div>
            <div className="content">
              <h3>{step.title}</h3>
              <p>{step.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HowItWorks;
