import React, { useState, useEffect } from "react";
import { Container, Box, Typography, Paper, Button, Snackbar, IconButton, List, ListItem, ListItemIcon, ListItemText, Checkbox, TextField, Divider, Select, MenuItem, FormControl, InputLabel } from "@mui/material";
import { Add, Delete, Edit, Brightness4, Brightness7 } from "@mui/icons-material";
import { AnimatePresence, motion } from "framer-motion";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { Chart } from "react-google-charts";

const TaskItem = ({ task, onToggle, onDelete, onEdit }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(task.title);
  const [editDueDate, setEditDueDate] = useState(task.dueDate);

  const handleSave = () => {
    onEdit(task.id, editTitle, editDueDate);
    setIsEditing(false);
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -10 }}
      transition={{ duration: 0.2 }}
    >
      <ListItem
        secondaryAction={
          <>
            <IconButton edge="end" onClick={() => setIsEditing((v) => !v)}>
              <Edit />
            </IconButton>
            <IconButton edge="end" onClick={() => onDelete(task.id)}>
              <Delete />
            </IconButton>
          </>
        }
      >
        <ListItemIcon>
          <Checkbox edge="start" checked={task.completed} onChange={() => onToggle(task.id)} />
        </ListItemIcon>
        {isEditing ? (
          <Box sx={{ display: "flex", flexDirection: "column", gap: 1, flex: 1 }}>
            <TextField
              value={editTitle}
              onChange={(e) => setEditTitle(e.target.value)}
              fullWidth
            />
            <DatePicker
              label="Due date"
              value={editDueDate}
              onChange={(newValue) => setEditDueDate(newValue)}
              renderInput={(params) => <TextField {...params} />}
            />
            <Button onClick={handleSave} variant="outlined" size="small">
              Save
            </Button>
          </Box>
        ) : (
          <ListItemText
            primary={`${task.title} ${task.dueDate ? `- Due: ${new Date(task.dueDate).toLocaleDateString()}` : ""}`}
            sx={{ textDecoration: task.completed ? "line-through" : "none" }}
          />
        )}
      </ListItem>
    </motion.div>
  );
};

export default function LockIn() {
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem("tasks");
    return saved ? JSON.parse(saved) : [];
  });
  const [input, setInput] = useState("");
  const [dueDate, setDueDate] = useState(null);
  const [filter, setFilter] = useState("all");
  const [deletedTask, setDeletedTask] = useState(null);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  const handleAddTask = () => {
    if (!input.trim()) return;
    setTasks((prev) =>
      [...prev, { id: Date.now(), title: input.trim(), completed: false, dueDate }]
        .sort((a, b) => new Date(a.dueDate || Infinity) - new Date(b.dueDate || Infinity))
    );
    setInput("");
    setDueDate(null);
  };

  const handleToggleTask = (id) => {
    setTasks((prev) =>
      prev.map((task) => (task.id === id ? { ...task, completed: !task.completed } : task))
    );
  };

  const handleDeleteTask = (id) => {
    const taskToDelete = tasks.find((t) => t.id === id);
    setDeletedTask(taskToDelete);
    setTasks((prev) => prev.filter((task) => task.id !== id));
    setSnackbarOpen(true);
  };

  const handleUndo = () => {
    if (deletedTask) setTasks((prev) => [...prev, deletedTask]);
    setDeletedTask(null);
    setSnackbarOpen(false);
  };

  const handleEditTask = (id, newTitle, newDueDate) => {
    setTasks((prev) =>
      prev.map((task) =>
        task.id === id ? { ...task, title: newTitle, dueDate: newDueDate } : task
      ).sort((a, b) => new Date(a.dueDate || Infinity) - new Date(b.dueDate || Infinity))
    );
  };

  const filteredTasks = tasks.filter((task) => {
    const today = new Date().toDateString();
    const due = task.dueDate ? new Date(task.dueDate).toDateString() : null;
    switch (filter) {
      case "completed": return task.completed;
      case "incomplete": return !task.completed;
      case "today": return due === today;
      default: return true;
    }
  });

  // Task Statistics Calculation
  const finishedTasks = tasks.filter((task) => task.completed && new Date(task.dueDate) >= new Date()).length;
  const failedTasks = tasks.filter((task) => !task.completed && new Date(task.dueDate) < new Date()).length;
  const totalTasks = tasks.length;
  const unfinishedTasks = totalTasks - finishedTasks;
  const productivityLevel = totalTasks === 0 ? 0 : ((finishedTasks / totalTasks) * 100);

  const theme = createTheme({ palette: { mode: darkMode ? "dark" : "light" } });

  // Pie chart data
  const pieChartData = [
    ["Task Type", "Count"],
    ["Finished Tasks", finishedTasks],
    ["Unfinished Tasks", unfinishedTasks],
    ["Failed Tasks", failedTasks],
  ];

  const options = {
    title: "Task Distribution",
    is3D: true,
    slices: {
      0: { offset: 0.1 },
      1: { offset: 0.1 },
      2: { offset: 0.1 },
    },
  };

  return (
    <div>
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <LocalizationProvider dateAdapter={AdapterDateFns}>
          <Container maxWidth="sm" sx={{ py: 4 }}>
            <Paper elevation={3} sx={{ p: 2 }}>
              <Box display="flex" justifyContent="space-between" alignItems="center">
                <Typography variant="h5">Google Tasks Clone</Typography>
                <IconButton onClick={() => setDarkMode((v) => !v)}>
                  {darkMode ? <Brightness7 /> : <Brightness4 />}
                </IconButton>
              </Box>

              <Box display="flex" gap={2} my={2}>
                <TextField
                  fullWidth
                  label="Add a new task"
                  variant="outlined"
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  onKeyDown={(e) => e.key === "Enter" && handleAddTask()}
                />
                <DatePicker
                  label="Due date"
                  value={dueDate}
                  onChange={(newValue) => setDueDate(newValue)}
                  renderInput={(params) => <TextField {...params} />}
                />
              </Box>

              <Button onClick={handleAddTask} variant="contained" startIcon={<Add />} fullWidth>
                Add Task
              </Button>

              <Divider sx={{ my: 2 }} />

              <FormControl fullWidth sx={{ mb: 2 }}>
                <InputLabel>Filter</InputLabel>
                <Select
                  value={filter}
                  label="Filter"
                  onChange={(e) => setFilter(e.target.value)}
                >
                  <MenuItem value="all">All</MenuItem>
                  <MenuItem value="completed">Completed</MenuItem>
                  <MenuItem value="incomplete">Incomplete</MenuItem>
                  <MenuItem value="today">Due Today</MenuItem>
                </Select>
              </FormControl>

              <List>
                <AnimatePresence>
                  {filteredTasks.map((task) => (
                    <TaskItem
                      key={task.id}
                      task={task}
                      onToggle={handleToggleTask}
                      onDelete={handleDeleteTask}
                      onEdit={handleEditTask}
                    />
                  ))}
                </AnimatePresence>
              </List>

              <Box sx={{ mt: 4 }}>
                <Typography variant="h6" align="center">
                  Productivity Level: {productivityLevel.toFixed(2)}%
                </Typography>

                <Chart
                  chartType
                  chartType="PieChart"
                  data={pieChartData}
                  options={options}
                  width="100%"
                  height="400px"
                />
              </Box>

            </Paper>
          </Container>

          <Snackbar
            open={snackbarOpen}
            autoHideDuration={6000}
            onClose={() => setSnackbarOpen(false)}
            message="Task deleted"
            action={<Button onClick={handleUndo}>Undo</Button>}
          />
        </LocalizationProvider>
      </ThemeProvider>
    </div>
  );
}
